INSERT dbo.RULES(ID_RULE, NAME, CONTENT, CREATED_DATE, MANAGER) VALUES (N'R0853', N'Đề nghị khách giữ gìn chìa/ thẻ khóa cẩn thận và nên gửi lại quầy lễ tân khi ra ', N'
