INSERT dbo.RULES(ID_RULE, NAME, CONTENT, CREATED_DATE, MANAGER) VALUES (N'R6289', N'Không thay đổi, di chuyển đồ đạc trong phòng', N'Quý khách vui lòng xuất trình hộ chiếu hoặc chứng minh nhân dân để làm thủ tục nhận phòng tại Lễ tân.', '2018-01-02 20:51:47.000', 'EMP21')
INSERT dbo.RULES(ID_RULE, NAME, CONTENT, CREATED_DATE, MANAGER) VALUES (N'R2481', N'Khách sạn chỉ chịu trách nhiệm với những tài sản hoặc tiền được gửi tại quầy Lễ ', N'
