INSERT dbo.RULES(ID_RULE, NAME, CONTENT, CREATED_DATE, MANAGER) VALUES (N'R4924', N'Không mang súng đạn, chất cháy nổ, chất độc hại, các chất gây nghiện, vật nuôi h', N'
