INSERT dbo.RULES(ID_RULE, NAME, CONTENT, CREATED_DATE, MANAGER) VALUES (N'R1918', N'Không thay đổi phòng hoặc đưa thêm người vào phòng khi chưa đăng ký trước với Lễ', N'
